% assigns MAPE to the individualsfunction fitness = Fitness(population,training,test)%finds the size of the population[length,~] = size(population);length = length;fitness = zeros(length,4);% assigns the individual's fittness scorefor i = 1:length    p = population(i,1);    d = population(i,2);    q = population(i,3);    ARIMA_test = arima(p,d,q);    [ARIMA_test,~,~] = estimate(ARIMA_test,training,'display','off');        residual = infer(ARIMA_test,test);    prediction = test + residual;    % calculates mean average percentage error    MAPE = mean(abs((test - prediction)./test))*100;    Acc = 100 - MAPE;    fitness(i,1) = Acc;    fitness(i,2) = p;    fitness(i,3) = d;    fitness(i,4) = q;endend