% assigns MAPE to the individualsfunction population = Fitness(population,training,test,len)%finds the size of the population[length,~] = size(population);% assigns the individual's fittness scorefor i = 1:length    % checks if the individual has been assigned a fitness score    if (population(i,1)) == 0            p = population(i,2);    d = population(i,3);    q = population(i,4);    ARIMA_test = arima(p,d,q);    [ARIMA_test,~,~] = estimate(ARIMA_test,training,'display','off');            prediction = forecast(ARIMA_test,len,'Y0',training);    % calculates mean average percentage error    MAPE = mean(abs((test - prediction)./test))*100;    ABB = 100 - MAPE;    population(i,1) = ABB;    endend